import matplotlib.pyplot as plt
import numpy as np
import os 
import imp

#############
# FUNCTIONS #
#############

def abs_to_rel( path ):
    """
    Description:
        Returns path relative to the present working directory 

    Arguments:
        path         # string that is the absolute path 

    Usage:
        datapath = rel_to_abs('/home/ab108/0VB/isatab/malariaAtlas/scripts/test')

        fi = open( datapath )
        while True:
            line = fi.readline()
            if line == '':
                break
            print line
        fi.close()
    """
    import os 
    return os.path.relpath( path, os.getcwd() )

########
# MAIN #
########

os.chdir(abs_to_rel('/home/ab108/0VB/2kb/scripts'))

motif_entropy = imp.load_source('motif_entropy.py', './motif_entropy.py')

path = abs_to_rel('/home/ab108/software/meme/db/CIS-BP/downloaded_format')

species_list = os.listdir(path)

###############################
# calculate entropy per motif #
###############################

species_to_motif_to_entropy = {}

entropy_arr = []

for species in species_list:

    species_to_motif_to_entropy[species] = {}

    path_motif = path+'/'+species+'/pwms_all_motifs/'

    motif_list = os.listdir(path_motif)

    for motif in motif_list:

        entropy = motif_entropy.fbp_cisbp(path_motif+motif)

        species_to_motif_to_entropy[species][motif] = entropy

    entropy_dist = np.array(species_to_motif_to_entropy[species].values())

    entropy_dist = entropy_dist[np.logical_not(np.isnan(entropy_dist))]

    entropy_arr.append(np.array(entropy_dist))


############
# Plotting #
############


